@prefix nrl: <http://www.semanticdesktop.org/ontology/yyyy/mm/dd/nrl#> .
@prefix ex: <http://www.example.org/vocabulary#> .

ex:kb2_metadata { 
    ex:kb2 rdf:type nrl:KnowledgeBase , 
                    nrl:GraphView ;
           nrl:viewOn ex:kb1 ;
           nrl:hasSpecification ex:rvs . 
	ex:rvs rdf:type nrl:RuleViewSpecification ;
           nrl:realizes ex:RDFSSemantics ;
           nrl:ruleLanguage `SPARQL` ;
           nrl:rule `CONSTRUCT {?s ?rdfs:subClassOf ?v} WHERE ...` ;
           nrl:rule `CONSTRUCT {?s ?rdf:type ?v} WHERE ...` . 
	ex:RDFSSemantics rdf:type nrl:Semantics ;
                     nrl:semanticsDefinedBy `http://www.w3.org/TR/2004/REC-rdf-mt-20040210/ ;
                     nrl:semanticsLabel `RDFS` . }

	ex:kb3_metadata {
    ex:kb3 rdf:type nrl:GraphView ,
                    nrl:KnowledgeBase ;
                    nrl:viewOn ex:kb2 ;
           nrl:hasSpecification ex:evs . 
	ex:evs rdf:type nrl:ExternalViewSpecification ;
           nrl:externalRealizer `GraphTaxonomyExtractor` . }

SparQL example query: "How many patients experienced side effects while taking Donepezil?".
Find more queries at http://www.w3.org/wiki/HCLSIG/PharmaOntology/Queries

PREFIX indivo: <http://indivo.org/vocab/xml/documents#>
PREFIX trans: <tag:eric@w3.org:2009/tmo/translator#>
PREFIX foaf: <http://xmlns.com/foaf/0.1/>

SELECT DISTINCT(?patient) ?patient_name ?encounter_date ?symptoms ?diagnosis ?drug_name ?prescription_start
WHERE {
    GRAPH <pchr> {
        ?patient foaf:name ?patient_name .
        ?encounter trans:patient ?patient ;
            trans:reportedSymptoms ?symptoms ;
            trans:workingDiagnosis ?diagnosis ;
            trans:dateOfEncounter ?encounter_date .
        ?drug trans:prescribedTo ?patient ;
            indivo:prescribedOn ?prescription_start ;
            trans:datePrescriptionEnded ?prescription_end ;
            trans:genericName ?drug_name .
        FILTER regex(?drug_name, `DONEPEZIL`, `i`) .
        FILTER (?prescription_start <= ?encounter_date && ?prescription_end >= ?encounter_date) .
    }
}

# Contexts

:XFiles a ome:Context ;
	rdfs:label `X-Files` ;
	ome:has-shadow :XFiles-MKSlash, :XFiles-MKGen .

:XFiles-MKSlash a ome:Context ;
	rdfs:label `X-Files (with M/K slash goggles)` ;
	ome:is-shadow-of :XFiles .

:XFiles-MKGen a ome:Context ;
	rdfs:label `X-Files (with gen goggles)` ;
	ome:is-shadow-of :XFiles .

# Timelines

:XFiles-TL a ome:Timeline ;
	rdfs:label `X-Files Timeline` .

:XFiles-Slash-TL a ome:Timeline ;
	rdfs:label `X-Files Slashers Timeline` .

:XFiles-Gen-TL a ome:Timeline ;
	rdfs:label `X-Files Gen Timeline` .

# Characters

:XF-Mulder a omb:Character ;
	rdfs:label `X-Files - Fox Mulder` ;
	ome:exists-in :XFiles .

:XF-Krycek a omb:Character ;
	rdfs:label `X-Files - Alex Krycek` ;
	ome:exists-in :XFiles .

# Events

:XF-TRatB-Kiss-Ambig a omea:Sex, ome:Social, omf:Interpretation ;
	rdfs:label `X-Files - K/M Kiss (The Red and the Black)` ;
	ome:has-subject-entity :XF-Krycek-Lips ;
	ome:has-object-entity :XF-Mulder-Face-Cheek-Right ;
	ome:exists-in :XFiles .

:XF-TRatB-Kiss-Innocent a ome:Social, omf:Interpretation ;
	rdfs:label `X-Files - K/M Kiss (The Red and the Black)` ;
	ome:has-subject-entity :XF-Krycek-Lips ;
	ome:has-object-entity :XF-Mulder-Face-Cheek-Right ;
	ome:exists-in :XFiles .

:XF-TRatB-Kiss-Slash a omea:Sex ;
	rdfs:label `X-Files - K/M Kiss (The Red and the Black)` ;
	ome:has-subject-entity :XF-Krycek-Lips ;
	ome:has-object-entity :XF-Mulder-Face-Cheek-Right ;
	ome:exists-in :XFiles-MKSlash .

:XF-TRatB-Kiss-Gen a ome:Social ;
	rdfs:label `X-Files - K/M Kiss (The Red and the Black)` ;
	ome:has-subject-entity :XF-Krycek-Lips ;
	ome:has-object-entity :XF-Mulder-Face-Cheek-Right ;
	ome:exists-in :XFilesMKGen .

# Occurrences

:XF-TRatB-Kiss-XFTL a ome:Occurrence ;
	rdfs:label `Krycek Kisses Mulder (The Red and the Black)` ;
	ome:timeline-ref :XFiles-TL ;
	ome:occurrence-of [
		a: rdf:Alt ;
		rdf:_1 :XF-TRatB-Kiss-Ambig ;
		rdf:_2 :XF-TRatB-Kiss-Innocent .
	] .

:XF-TRatB-Kiss-XFSTL a ome:Occurrence ;
	rdfs:label `Krycek Kisses Mulder (The Red and the Black)` ;
	ome:timeline-ref :XFiles-Slash-TL ;
	ome:occurrence-of :XF-TRatB-Kiss-Slash.

:XF-TRatB-Kiss-XFGTL a ome:Occurrence ;
	rdfs:label `Krycek Kisses Mulder (The Red and the Black)` ;
	ome:timeline-ref :XFiles-Gen-TL ;
	ome:occurrence-of :XF-TRatB-Kiss-Gen.

The following example shows how Fresnel can be used to render FOAF persons.

@prefix fresnel:  <http://www.w3.org/2004/09/fresnel#> .
@prefix foaf:     <http://xmlns.com/foaf/0.1/> .
@prefix rdf:      <http://www.w3.org/1999/02/22-rdf-syntax-ns#> .
@prefix xsd:      <http://www.w3.org/2001/XMLSchema#> .
@prefix :         <#>

# -----------------------------------------------
# 1.Fresnel Lenses for FOAF Person
# -----------------------------------------------

:foafPersonDefaultLens rdf:type fresnel:Lens ;
                       fresnel:purpose fresnel:defaultLens ;
                       fresnel:classLensDomain foaf:Person ;
                       fresnel:group :foafGroup ;
                       fresnel:showProperties ( foaf:name 
                                                foaf:surname 
                                                foaf:depiction ) .

:knowsLens rdf:type fresnel:Lens ;
           fresnel:classLensDomain foaf:Person ;
           fresnel:group :foafGroup ;
           fresnel:showProperties ( foaf:name 
                                    foaf:surname
                                    foaf:mbox 
                                    [ rdf:type fresnel:PropertyDescription ;
                                      fresnel:property foaf:knows ;
                                      fresnel:sublens :foafPersonDefaultLens ] ) .

# -----------------------------------------------
# 2. Fresnel Formats
# -----------------------------------------------

:foafGroup rdf:type fresnel:Group ;
           fresnel:stylesheetLink <http://www.example.org/example.css> . 

:depictFormat rdf:type fresnel:Format ;
              fresnel:propertyFormatDomain foaf:depiction ;
              fresnel:label fresnel:none ;
              fresnel:value fresnel:image ;
              fresnel:valueStyle `imageWithThickBorder`^^fresnel:styleClass ; 
              fresnel:group :foafGroup .

:urlFormat rdf:type fresnel:Format ;
           fresnel:propertyFormatDomain foaf:homepage ;
           fresnel:propertyFormatDomain foaf:mbox ;
           fresnel:value fresnel:externalLink ;
           fresnel:labelStyle `basicLabel`^^fresnel:styleClass ;
           fresnel:valueStyle `basicUrl`^^fresnel:styleClass ;
           fresnel:group :foafGroup .

:telecon
      a       :Interval ;
      :hasBeginning :teleconStart .

:meeting
      a       :Interval ;
      :hasBeginning :meetingStart ;
      :hasDurationDescription
              :meetingDuration .

:teleconStart
      a       :Instant ;
      :inXSDDateTime
              2006-11-05T18:00:00-5:00 .

:meetingStart
      a       :Instant ;
      :inXSDDateTime
              2006-11-05T14:00:00-8:00 .

:meetingDuration
      a       :DurationDescription ;
      :minutes 45 .

PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
PREFIX spatial: <http://data.ordnancesurvey.co.uk/ontology/spatialrelations/>
PREFIX gaz: <http://data.ordnancesurvey.co.uk/ontology/50kGazetteer/>

SELECT ?uri ?label ?easting ?northing ?one ?twenty ?map 
WHERE {
  ?uri 
    #filter on type
    gaz:featureType gaz:RomanAntiquity;

    #bind everything we want to return
    rdfs:label ?label;
    spatial:easting ?easting;
    spatial:northing ?northing;
    gaz:oneKMGridReference ?one;
    gaz:twentyKMGridReference ?twenty;
    gaz:mapReference ?map.
}

