:author-1 pro:holdsRoleInTime :role1 .
:role1 ?a pro:RoleInTime ;
   pro:withRole scoro:principal-author ;
   tvc:withinContext :ourJointPaper .

:author-2 pro:holdsRoleInTime :role2 .

:role2 ?a pro:RoleInTime ;
   pro:withRole scoro:principal-author ;
   tvc:withinContext :ourJointPaper ;
   scoro:isEqualToRoleInTime :role1 .

:ourJointPaper a fabio:JournalArticle ;
   dcterms:creator :author-1 , author-2 .

:my-paper pso:holdsStatusInTime
    :submitted , :under-review, :accepted .

:submitted a pso:StatusInTime ;
    pso:withStatus pso:submitted ;
    tvc:atTime [ a ti:TimeInterval ;
        ti:hasIntervalStartDate `2012-04-24T13:18:21Z`^^xsd:dateTime ];
    pso:isAcquiredAsConsequenceOf :author-submits-paper ] .

:under-review a pso:StatusInTime ;
    pso:withStatus pso:under-review ;
    tvc:atTime [ a ti:TimeInterval ;
        ti:hasIntervalStartDate `2012-04-26T12:00:00Z`^^xsd:dateTime ;
        ti:hasIntervalEndDate `2012-05-27T17:38:01Z`^^xsd:dateTime ] ;
    pso:isAcquiredAsConsequenceOf :reviewers-sent-paper ;
    pso:isLostAsConsequenceOf :reviewers-finish-reviewing .

:accepted a pso:StatusInTime ;
    pso:withStatus pso:accepted ;
    tvc:atTime [ a ti:TimeInterval ;
        ti:hasIntervalStartDate `2012-05-28T10:22:31Z`^^xsd:dateTime ] ;
    pso:isAcquiredAsConsequenceOf :paper-accepted ] .

:workflow a pwo:Workflow ;
    pwo:hasFirstStep :stepOne  ;
    pwo:hasStep :stepTwo , :stepThree ,  :stepFour  .

:stepOne a pwo:Step ; # Author writes paper
    pwo:involvesEvent [ a part:Event ;
        dcterms:description `Author writes the paper` ;
        part:hasParticipant :author ] ;
    tisit:atTime [ a ti:TimeInterval ;
        ti:hasIntervalStartDate `2009-02-14T00:00:00Z`^^xsd:dateTime ;
        ti:hasIntervalEndDate `2009-03-25T00:00:00Z`^^xsd:dateTime ] ;
    pwo:produces :my-paper ;
    pwo:hasNextStep :stepTwo .

:stepTwo a pwo:Step ; # Author submits paper
    pwo:needs :my-paper;
    pwo:involvesEvent [ a part:Event ; 
        dcterms:description `Author submits the paper` ; 
        part:hasParticipant :author ] ; 
    tisit:atTime [ a ti:TimeInterval ; 
        ti:hasIntervalStartDate `2009-04-24T13:18:21Z`^^xsd:dateTime ; 
        ti:hasIntervalEndDate `2009-04-24T13:18:21Z`^^xsd:dateTime ] ; 
    pwo:produces :submitted ; # New status-in-time for :my-paper
    pwo:hasNextStep :stepThree . 

:stepThree a pwo:Step ; # Paper under review 
    pwo:needs :my-paper ;
    pwo:involvesEvent [ a part:Event ; 
        dcterms:description `Reviewers review the paper` ; 
        part:hasParticipant :reviewer1, :reviewer2 ] ; 
    tisit:atTime [ a ti:TimeInterval ; 
        ti:hasIntervalStartDate `2009-04-26T12:00:00Z`^^xsd:dateTime ; 
        ti:hasIntervalEndDate `2009-05-26T12:00:00Z`^^xsd:dateTime ] ; 
    pwo:produces :review1 , :review2; 
    pwo:hasNextStep :stepFour . 

:stepFour a pwo:Step ; # Notification of acceptance 
    pwo:needs :review1 , :review2; 
    pwo:involvesEvent [ a part:Event ; 
        dcterms:description `Editor sends acceptance notification` ; 
        part:hasParticipant :editor ] ; 
    tisit:atTime [ a ti:TimeInterval ; 
        ti:hasIntervalStartDate `2009-04-26T12:00:00Z`^^xsd:dateTime ; 
        ti:hasIntervalEndDate `2009-05-27T17:38:01Z`^^xsd:dateTime ] ; 
    pwo:produces :accepted , :acceptance-notification . 
            # New status-in-time for the paper, and a new document

